const AudioManager = {
  sounds: {},
  music: null,
  
  init() {
    // 효과음 로드
    this.loadSound('shoot', 'data:audio/mp3;base64,');
    this.loadSound('hit', 'data:audio/mp3;base64,');
    this.loadSound('reload', 'data:audio/mp3;base64,');
    this.loadSound('jump', 'data:audio/mp3;base64,');
    this.loadSound('emptyGun', 'data:audio/mp3;base64,');
    
    // 새로운 효과음 (v0.0.6 추가)
    this.loadSound('bonusHit', 'data:audio/mp3;base64,');
    this.loadSound('penaltyHit', 'data:audio/mp3;base64,');
    
    // 배경음악 로드
    this.music = new Audio('data:audio/mp3;base64,');
    this.music.loop = true;
    this.music.volume = GameSettings.volumes.music;
  },
  
  loadSound(name, url) {
    this.sounds[name] = new Audio(url);
    this.sounds[name].volume = GameSettings.volumes.sfx;
  },
  
  play(name) {
    if (this.sounds[name]) {
      // 사운드 중복 재생 방지를 위해 클론 생성
      const sound = this.sounds[name].cloneNode();
      sound.volume = GameSettings.volumes.sfx;
      sound.play();
    }
  },
  
  playMusic() {
    if (this.music && this.music.paused) {
      this.music.play();
    }
  },
  
  pauseMusic() {
    if (this.music && !this.music.paused) {
      this.music.pause();
    }
  },
  
  setVolume(type, volume) {
    if (type === 'music' && this.music) {
      this.music.volume = volume;
    } else if (type === 'sfx') {
      for (const sound in this.sounds) {
        this.sounds[sound].volume = volume;
      }
    }
  }
};
